// Local shim that dynamically loads Three.js r134 from CDN.
(function(){
  if (window.THREE) return;
  var s = document.createElement('script');
  s.src = 'https://cdnjs.cloudflare.com/ajax/libs/three.js/r134/three.min.js';
  s.async = false;
  s.onload = function(){ console.log('[three.r134.min.js] Loaded Three.js from CDN'); };
  s.onerror = function(){ console.error('[three.r134.min.js] Failed to load Three.js from CDN'); };
  document.head.appendChild(s);
})();
