// Local shim that dynamically loads Vanta.NET from CDN.
(function(){
  if (window.VANTA && window.VANTA.NET) return;
  var s = document.createElement('script');
  s.src = 'https://cdn.jsdelivr.net/npm/vanta@0.5.24/dist/vanta.waves.min.js';
  s.async = false;
  s.onload = function(){ console.log('[vanta.net.min.js] Loaded Vanta.NET from CDN'); };
  s.onerror = function(){ console.error('[vanta.net.min.js] Failed to load Vanta.NET from CDN'); };
  document.head.appendChild(s);
})();
